USE thcpl;



CREATE TABLE category(
	id INT,
	name VARCHAR(30)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

CREATE TABLE message(
	cat INT,
	isTrans INT,
	isA INT,
	name VARCHAR(30),
	fullname VARCHAR(100),
	deadline VARCHAR(50),
	restime VARCHAR(50),
	page VARCHAR(50),
	site VARCHAR(100)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

